    private void btnVerificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVerificarActionPerformed
        // TODO add your handling code here:
        int na = Integer.parseInt(lblLado1.getText());
        int nb = Integer.parseInt(lblLado2.getText());
        int nc = Integer.parseInt(lblLado3.getText());

        if (na == nb && nb == nc && nc == na){
            txtResult.setText("Triangulo Equil√°tero");
        }  
        if (na != nb && nb != nc && nc != na ){
            txtResult.setText("Triangulo Escaleno");
        }
        if (na == nb && nb != nc && nc != na ){
            txtResult.setText("Triangulo Isosceles");
        }  
        if (na != nb && nb == nc && nc != na ){
            txtResult.setText("Triangulo Isosceles");
        }
        if (na != nb && nb != nc && nc == na ){
            txtResult.setText("Triangulo Isosceles");
        }
    }//GEN-LAST:event_btnVerificarActionPerformed

